import unittest


def reduce(string):
    occured = set()
    result = ""
    for c in string:
        if c in occured:
            continue
        occured.add(c)
        result += c
    return result


def merge_the_tools(string, k):
    sub_len = len(string) // k
    parts = []
    for i in range(0, len(string), sub_len):
        parts.append(reduce(string[i : i + sub_len]))

    return parts


class TestStringMethods(unittest.TestCase):
    def test_a(self):
        self.assertEqual(merge_the_tools("AABCAAADA", 3), ["AB", "CA", "AD"])

    def test_b(self):
        self.assertEqual(
            merge_the_tools(
                "",
                2,
            ),
            ["KYQTWXDLINFBHRGZVCUSAMOEPJ", "YUGTZIWNVSALBXOCDMPFEKJRQH"],
        )


if __name__ == "__main__":
    unittest.main()
